mapper.select:
SELECT A.SALE_PRC
     , A.SOLD_QTY
  FROM TBL_SELLING A
 WHERE A.CORP_CD = #{corpCd}
   AND (A.SELLING_ST = '2'
       OR A.SELLING_ST = '3'
       )
   AND A.UPDATE_DATE >= #{frDt}
   AND A.UPDATE_DATE < #{toDt} + 1

mapper.update:
UPDATE TBL_QNA
   SET QNA_ANS = #{qnaAns}
     , QNA_ST = '2'
     , UPDATE_DATE = CURRENT_TIMESTAMP
 WHERE CORP_CD = #{corpCd}
   AND QNA_NO = #{qnaNo}
