mapper.select:
SELECT A.ITEM_CD , A.CORP_CD , B.ITEM_NM , A.ITEM_BARCODE , A.SALE_PRC , A.SALE_AMT , A.DISCOUNT_RAT , A.ITEM_EXPDATE FROM TBL_GOODS A LEFT OUTER JOIN TBL_ITEM B ON A.ITEM_CD = B.ITEM_CD WHERE A.CORP_CD = #{corpCd} AND B.CORP_CD = #{corpCd}
AND ( A.ITEM_CD iLIKE '%' ||#{itemCdNm}|| '%' OR B.ITEM_NM iLIKE '%'||#{itemCdNm}||'%' )
AND A.ITEM_BARCODE = #{itemBarcode}

SELECT A.ITEM_CD , A.CORP_CD , B.ITEM_NM , A.ITEM_BARCODE , A.SALE_PRC , A.DISCOUNT_RAT , A.ITEM_EXPDATE , A.ITEM_QTY , A.ORDERER_NAME , A.SOLD_QTY FROM TBL_SELLING A LEFT OUTER JOIN TBL_ITEM B ON A.ITEM_CD = B.ITEM_CD WHERE A.CORP_CD = #{corpCd} AND B.CORP_CD = #{corpCd}
AND ( A.ITEM_CD iLIKE '%' ||#{itemCdNm}|| '%' OR B.ITEM_NM iLIKE '%'||#{itemCdNm}||'%' )
AND A.ITEM_BARCODE = #{itemBarcode}
