mapper.insert:
INSERT INTO TBL_GOODS (
                       ITEM_CD
                     , CORP_CD
                     , ITEM_BARCODE
                     , SALE_PRC
                     , SALE_AMT
                     , DISCOUNT_RAT
                     , ITEM_EXPDATE
                     , INSERT_DATE
                     , UPDATE_DATE
)VALUES (
          #{itemCd}
        , #{corpCd}
        , #{itemBarcode}
        , #{salePrc}
        , #{saleAmt}
        , #{discountRat}
        , #{itemExpdate}
        , CURRENT_TIMESTAMP
        , CURRENT_TIMESTAMP
        )

mapper.update:
UPDATE TBL_GOODS
SET ITEM_CD = #{itemCd}
  , ITEM_BARCODE = #{itemBarcode}
  , SALE_PRC = #{salePrc}
  , SALE_AMT = #{saleAmt}
  , DISCOUNT_RAT = #{discountRat}
  , UPDATE_DATE = CURRENT_TIMESTAMP
WHERE CORP_CD = #{corpCd}
  AND ITEM_CD = #{itemCd}

mapper.delete:
DELETE FROM TBL_GOODS
WHERE ITEM_CD = #{itemCd}
  AND CORP_CD = #{corpCd}

mapper.select:
SELECT A.ITEM_CD , A.CORP_CD , B.ITEM_NM , A.ITEM_BARCODE , A.SALE_PRC , A.SALE_AMT , A.DISCOUNT_RAT , A.ITEM_EXPDATE FROM TBL_GOODS A LEFT OUTER JOIN TBL_ITEM B ON A.ITEM_CD = B.ITEM_CD WHERE A.CORP_CD = #{corpCd}
AND ( A.ITEM_CD iLIKE '%' ||#{itemCdNm}|| '%' OR B.ITEM_NM iLIKE '%'||#{itemCdNm}||'%' )
AND A.ITEM_BARCODE = #{itemBarcode}
