mapper.insert:
INSERT INTO TBL_ITEM (
            ITEM_CD
          , CORP_CD
          , ITEM_NM
          , ITEM_BARCODE
          , ITEM_PRC
          , USE_YN
          , INSERT_DATE
          , UPDATE_DATE
)VALUES (
        #{itemCd}
      , #{corpCd}
      , #{itemNm}
      , #{itemBarcode}
      , #{itemPrc}
      , true
      , CURRENT_TIMESTAMP
      , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE TBL_ITEM
   SET ITEM_NM = #{itemNm}
     , ITEM_BARCODE = #{itemBarcode}
     , ITEM_PRC = #{itemPrc}
 WHERE CORP_CD = #{corpCd}
   AND ITEM_CD = #{itemCd}

mapper.select:
SELECT A.ITEM_CD , A.CORP_CD , A.ITEM_NM , A.ITEM_BARCODE , A.ITEM_PRC , A.USE_YN , A.INSERT_DATE FROM TBL_ITEM A WHERE A.USE_YN = true AND A.CORP_CD = #{corpCd}
AND ( A.ITEM_CD iLIKE '%' ||#{itemCdNm}|| '%' OR A.ITEM_NM iLIKE '%'||#{itemCdNm}||'%' )
AND A.ITEM_BARCODE = #{itemBarcode}
