mapper.update:
UPDATE TBL_QNA
   SET QNA_ANS = #{qnaAns}
     , QNA_ST = '답변완료'
     , UPDATE_DATE = CURRENT_TIMESTAMP
 WHERE QNA_NO = #{qnaNo}

mapper.select:
SELECT A.NOTICE_NO
     , A.NOTICE_TIT
     , A.NOTICE_TXT
     , A.NOTICE_TODT
     , A.NOTICE_FRDT
     , A.INSERT_DATE
  FROM TBL_NOTICE A
 WHERE A.NOTICE_FRDT > #{noticeFrdt}
   AND A.NOTICE_TODT <= #{noticeTodt}
   AND A.NOTICE_TP = #{noticeTp}

    ORDER BY A.INSERT_DATE
    LIMIT #{limitNum}

mapper.insert:
INSERT INTO TBL_NOTICE (
            NOTICE_NO
          , NOTICE_TIT
          , NOTICE_TXT
          , NOTICE_TODT
          , NOTICE_FRDT
          , INSERT_DATE
          , UPDATE_DATE
          , NOTICE_TP
)VALUES (
          #{noticeNo}
        , #{noticeTit}
        , #{noticeTxt}
        , #{noticeTodt}
        , #{noticeFrdt}
        , CURRENT_TIMESTAMP
        , CURRENT_TIMESTAMP
        , #{noticeTp}
        )

mapper.update:
UPDATE TBL_NOTICE
   SET NOTICE_TIT = #{noticeTit}
     , NOTICE_TXT = #{noticeTxt}
     , NOTICE_TODT = #{noticeTodt}
     , NOTICE_FRDT = #{noticeFrdt}
     , UPDATE_DATE = CURRENT_TIMESTAMP
     , NOTICE_TP = #{noticeTp}
WHERE NOTICE_NO = #{noticeNo}
