mapper.insert:
INSERT INTO TBL_ITEM_IMG (
                        ITEM_CD
                      , CORP_CD
                      , IMG_NM
                      , IMG_LOC
                      , USER_ID
                      , IMG_ID
)VALUES (
          nextval('SEQ_ITEM')
        , #{corpCd}
        , #{imgNm}
        , #{imgLoc}
        , #{userId}
        , #{imgId}
        )

mapper.update:
UPDATE TBL_GOODS
SET ITEM_CD = #{itemCd}
  , ITEM_BARCODE = #{itemBarcode}
  , SALE_PRC = #{salePrc}
  , SALE_AMT = #{saleAmt}
  , DISCOUNT_RAT = #{discountRat}
  , UPDATE_DATE = CURRENT_TIMESTAMP
WHERE CORP_CD = #{corpCd}
  AND ITEM_CD = #{itemCd}

mapper.delete:
DELETE FROM TBL_GOODS
WHERE ITEM_CD = #{itemCd}
  AND CORP_CD = #{corpCd}
