mapper.insert:
INSERT INTO TBL_USERS (
            USER_NM
          , USER_ID
          , USER_PW
          , USER_NICK
          , USER_EMAIL
          , USER_ADDR
          , USER_PHONE_NUMBER
          , USE_YN
          , INSERT_DATE
          , UPDATE_DATE
)VALUES (
        #{userNm}
      , #{userId}
      , #{userPw}
      , #{userNick}
      , #{userEmail}
      , #{userAddr}
      , #{userPhoneNumber}
      , true
      , CURRENT_TIMESTAMP
      , CURRENT_TIMESTAMP
)

mapper.select:
SELECT COUNT(A.USER_ID)
  FROM TBL_USERS A
 WHERE 1 = 1

   AND A.USER_ID = #{userId}


   AND A.USER_NICK = #{userNick}
