mapper.select:
SELECT A.USER_NM
     , A.USER_ID
     , A.USER_PW
  FROM TBL_USERS A
 WHERE USER_ID = #{userId}
   AND USER_PW = #{userPw}

SELECT A.USER_ID
FROM TBL_USERS A
WHERE 1=1

    AND A.USER_EMAIL = #{userEmail}


    AND A.USER_PHONE_NUMBER = #{userPhoneNumber}

SELECT COUNT(A.USER_ID)
  FROM TBL_USERS A
 WHERE A.USER_ID = #{userId}

mapper.update:
UPDATE TBL_USERS
SET USER_PW = #{userPw}
WHERE USER_ID = #{userId}
