mapper.insert:
INSERT INTO TBL_BASKET (
            CORP_CD
          , ITEM_CD
          , SALE_PRC
          , ITEM_EXPDATE
          , USER_ID
          , PURCHASE_ST
          , ITEM_QTY
          , ITEM_NM
)VALUES (
        #{corpCd}
      , #{itemCd}
      , #{salePrc}
      , #{itemExpdate}
      , #{userId}
      , '주문전'
      , #{itemQty}
      , #{itemNm}
)

mapper.delete:
DELETE FROM TBL_BASKET
 WHERE CORP_CD = #{corpCd}
   AND USER_ID = #{userId}
   AND ITEM_CD = #{itemCd}

mapper.update:
UPDATE TBL_BASKET
   SET ITEM_QTY = #{itemQty}
 WHERE CORP_CD = #{corpCd}
   AND USER_ID = #{userId}
   AND ITEM_CD = #{itemCd}
