mapper.insert:
INSERT INTO TBL_QNA (
          QNA_NO
        , USER_ID
        , QNA_TIT
        , QNA_TXT
        , QNA_ST
        , QNA_TP
        , INSERT_DATE
) VALUES (
           QNA_NO = #{qnaNo}
         , QNA_TIT = #{qnaTit}
         , QNA_TXT = #{qnaTxt}
         , UPDATE_DATE = CURRENT_TIMESTAMP

)

mapper.update:
UPDATE TBL_QNA
SET QNA_TIT = #{qnaTit}
  , QNA_TXT = #{qnaTxt}
  , UPDATE_DATE = CURRENT_TIMESTAMP
WHERE QNA_NO = #{qnaNo}
